mapper.select:
SELECT b.*, U.CHINESE_NAME as chineseName, U.RESIDENTIAL_ADDRESS AS residentialAddress, U.CARD_TYPE AS cardType, U.CARD_ID AS cardId, b.APPLY_QUOTA AS applyQuota, A.SERIAL_NUMBER as SERIALNUMBER, N.NODE_NAME as nodeName, A.AUDIT_TIME as auditTime FROM CUSTOMER_APPLICATION_INFO b, BASIC_CUSTOMER_INFORMATION U, CUSTOMER_APPLICATION_PROCESS A, NODE_AUDIT N,NODE_AUDIT_USER p WHERE 1=1 and U.ID = b.CUSTOMER_ID and a.APPLICATION_ID=b.ID AND N.ID=A.NEXT_NODE_ID and A.NEXT_NODE_ID=p.NODE_ID and p.USER_ID=#{loginId} and b.status='audit' and N.IS_RECIEVE=#{isReceive}
and U.card_id=#{cardId}
and U.chinese_name like '%'||#{chineseName}||'%'
and N.NODE_NAME=#{nextNodeName}

SELECT COUNT (*) FROM ( SELECT b.*, U.CHINESE_NAME as chineseName, U.RESIDENTIAL_ADDRESS AS residentialAddress, U.CARD_TYPE AS cardType, U.CARD_ID AS cardId, b.APPLY_QUOTA AS applyQuota, A.SERIAL_NUMBER as SERIALNUMBER, N.NODE_NAME as nodeName, A.AUDIT_TIME as auditTime FROM CUSTOMER_APPLICATION_INFO b, BASIC_CUSTOMER_INFORMATION U, CUSTOMER_APPLICATION_PROCESS A, NODE_AUDIT N,NODE_AUDIT_USER p WHERE 1=1 and U.ID = b.CUSTOMER_ID and a.APPLICATION_ID=b.ID AND N.ID=A.NEXT_NODE_ID and A.NEXT_NODE_ID=p.NODE_ID and p.USER_ID=#{loginId} and b.status='audit' and N.IS_RECIEVE=#{isReceive}
and U.card_id=#{cardId}
and U.chinese_name like '%'||#{chineseName}||'%'
and N.NODE_NAME=#{nextNodeName}
)

SELECT b.*, U.CHINESE_NAME as chineseName, U.RESIDENTIAL_ADDRESS AS residentialAddress, U.CARD_TYPE AS cardType, U.CARD_ID AS cardId, b.APPLY_QUOTA AS applyQuota, A.SERIAL_NUMBER as SERIALNUMBER, N.NODE_NAME as nodeName, A.AUDIT_TIME as auditTime, A.id as processId, A.if_recieved as ifRecieved,b.id as applicationId FROM CUSTOMER_APPLICATION_INFO b, BASIC_CUSTOMER_INFORMATION U, CUSTOMER_APPLICATION_PROCESS A, NODE_AUDIT N,NODE_AUDIT_USER p WHERE 1=1 and U.ID = b.CUSTOMER_ID and a.APPLICATION_ID=b.ID AND N.ID=A.NEXT_NODE_ID and A.NEXT_NODE_ID=p.NODE_ID and p.USER_ID=#{loginId} and b.status='audit' and N.IS_RECIEVE=#{isReceive}
and A.if_recieved=#{ifRecieved}
and A.if_recieved is null
and U.card_id=#{cardId}
and U.chinese_name like '%'||#{chineseName}||'%'
and A.fuhe_user=#{fuheUser}

select count(*) from ( SELECT b.*, U.CHINESE_NAME as chineseName, U.RESIDENTIAL_ADDRESS AS residentialAddress, U.CARD_TYPE AS cardType, U.CARD_ID AS cardId, b.APPLY_QUOTA AS applyQuota, A.SERIAL_NUMBER as SERIALNUMBER, N.NODE_NAME as nodeName, A.AUDIT_TIME as auditTime FROM CUSTOMER_APPLICATION_INFO b, BASIC_CUSTOMER_INFORMATION U, CUSTOMER_APPLICATION_PROCESS A, NODE_AUDIT N,NODE_AUDIT_USER p WHERE 1=1 and U.ID = b.CUSTOMER_ID and a.APPLICATION_ID=b.ID AND N.ID=A.NEXT_NODE_ID and A.NEXT_NODE_ID=p.NODE_ID and p.USER_ID=#{loginId} and b.status='audit' and N.IS_RECIEVE=#{isReceive}
and A.if_recieved=#{ifRecieved}
and A.if_recieved is null
and U.card_id=#{cardId}
and U.chinese_name like '%'||#{chineseName}||'%'
and A.fuhe_user=#{fuheUser} )

mapper.update:
update customer_application_process

	audit_User='',
	audit_Time=#{auditTime},
	delay_audit_user=#{delayAuditUser}

where serial_number=#{serialNumber}

	update customer_application_process t set t.delay_audit_user = ''
	where t.application_id in(
			select ad.id from customer_application_info ad 
			where ad.status='audit'
         and ad.modified_time<sysdate - #{maxDay}
		)
