mapper.select:
SELECT * FROM (SELECT ROW_.*,ROWNUM ROWNUM_ FROM ( select SQ.*,INFO.status as infoStatus,INFO.nodeName,INFO.USER_ID AS applnId,INFO.ID AS processId from XM_NEW_SQ SQ LEFT JOIN (SELECT b.*, A .SERIAL_NUMBER AS SERIALNUMBER, N.NODE_NAME AS nodeName, A . ID AS processId, p.USER_ID FROM XM_NEW_APPLICATION_INFO b, CUSTOMER_APPLICATION_PROCESS A, NODE_AUDIT N, NODE_AUDIT_USER P WHERE 1 = 1 AND A .APPLICATION_ID = b. ID AND N. ID = A .NEXT_NODE_ID AND A .NEXT_NODE_ID = P .NODE_ID) INFO on SQ.id=INFO.sq_id where 1=1
and INFO.USER_ID=#{applnId}
and SQ.sq_name like '%'||#{name}||'%'
and SQ.org_name like '%'||#{orgName}||'%'
) ROW_ WHERE ROWNUM <= #{limit}*#{page} + #{limit}) WHERE ROWNUM_ > #{limit}*#{page}

SELECT * FROM (SELECT ROW_.*,ROWNUM ROWNUM_ FROM ( SELECT SQ.*,b.status AS infoStatus, N.NODE_NAME AS nodeName, A . ID AS processId, P .USER_ID AS applnId FROM XM_NEW_SQ SQ, XM_NEW_APPLICATION_INFO b, CUSTOMER_APPLICATION_PROCESS A, NODE_AUDIT N, NODE_AUDIT_USER P WHERE 1 = 1 and SQ. ID = b.sq_id AND A .APPLICATION_ID = b. ID AND N. ID = A .NEXT_NODE_ID AND A .NEXT_NODE_ID = P .NODE_ID
and (P .USER_ID=#{applnId} or b.zb_user=#{applnId} )
and SQ.sq_name like '%'||#{name}||'%'
and SQ.org_name like '%'||#{orgName}||'%'
) ROW_ WHERE ROWNUM <= #{limit}*#{page} + #{limit}) WHERE ROWNUM_ > #{limit}*#{page}
